INSERT into product(id, name, online_status, long_description, short_description) values ('236', '1404536 DN-MC 6000', 'ACTIVE', 'Der Professional Digital Mixer & Controller MC6000 MKII von DENON DJ ist der Nachfolge des erfolgreichen
