INSERT into product(id, name, online_status, long_description, short_description) values ('905', '2168386 NC 50 DAB-B', 'ACTIVE', 'All-In-One-HiFi-System mit Class-D-Endstufen, CD-Player und voll ausgestatteter Streaming-Einheit.

