INSERT into product(id, name, online_status, long_description, short_description) values ('906', '2168388 N-30 AE-B', 'ACTIVE', 'Netzwerk-Player mit integriertem Dualband-WiFi, Airplay, DLNA-Streaming mit Gapless-, HiRes- und DSD-Fähigkeit, TuneIn-Internetradio und brillantem 3.5  -TFT-Display

